import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const MenuBar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <nav style={{ marginBottom: '20px', padding: '10px', backgroundColor: '#007bff', color: 'white' }}>
      <Link to="/dashboard" style={{ color: 'white', marginRight: '10px' }}>Dashboard</Link>
      <Link to="/add-task" style={{ color: 'white', marginRight: '10px' }}>Add Task</Link>
      <Link to="/report-issue" style={{ color: 'white', marginRight: '10px' }}>Report Issue</Link>
      <button onClick={handleLogout} style={{ backgroundColor: 'white', color: '#007bff', border: 'none', cursor: 'pointer' }}>Logout</button>
    </nav>
  );
};

export default MenuBar;
